mapper.select:
select id, agency_id, grade_id, class_id, name, state, updated_time, created_time,group_id
from pq_agency_class
where id = #{id,jdbcType=BIGINT}

select id, agency_id, grade_id, class_id, name, state, updated_time, created_time,group_id
from pq_agency_class
where agency_id = #{agencyId}
and grade_id = #{gradeId}
and state = 1

select id, agency_id, grade_id, class_id, name, state, updated_time, created_time,group_id
from pq_agency_class
where agency_id = #{agencyId}
    and grade_id = #{gradeId}
    and class_id = #{classId}
and state = 1
