mapper.select:
select id, agency_class_id, name, sex, avatar, state, updated_time, created_time
from pq_agency_student
where id = #{id,jdbcType=BIGINT}

select id, agency_class_id, name, sex, avatar, state, updated_time, created_time
from pq_agency_student
where agency_class_id =#{agencyClassId}
and id = #{id}
and state = 1

select id, agency_class_id, name, sex, avatar, state, updated_time, created_time
from pq_agency_student
where agency_class_id =#{agencyClassId}
and name = #{name}
and state = 1
