mapper.select:
select id, agency_class_id, user_id, role, state, updated_time, created_time, is_head
from pq_agency_user
where id = #{id,jdbcType=BIGINT}

select  id, agency_class_id, user_id, role, state, updated_time, created_time,is_head
from pq_agency_user
where user_id = #{userId}

  and agency_class_id = #{agencyClassId}

and state = 1

select  count(id)
from pq_agency_user
where agency_class_id = #{agencyClassId}
and role = #{role}
and state = 1

select  id, agency_class_id, user_id, role, state, updated_time, created_time, is_head
