mapper.select:
select id, class_course_id, user_id, state, updated_time, created_time
from pq_user_course
where id = #{id,jdbcType=BIGINT}

select u.*
from pq_user_course u, pq_class_course c
where u.user_id = #{userId}
and c.class_id = #{classId}
and u.class_course_id = c.id
and u.state = 1

select id, class_course_id, user_id, state, updated_time, created_time
from pq_user_course
where user_id = #{userId}
and class_course_id = #{classCourseId}
and state = 1
